import React, { useState } from "react";
import { getAllPosts, addPosts, deletePosts } from "../api";





const Posts = (props) => {
    const handleSubmit = async (event) => {
        event.preventDefault();
        const posts = {
            title: title,
            description: description,
            price: price
        }
        const data = await addPosts(posts);
    }

    const deleteSubmit = async (event) => {
        event.preventDefault();
        let id
        posts.map(post => {
            if (post.title == title) {
                id = post._id
            }
        })
        console.log(title)
        const data = await deletePosts(id);
    }

    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState('');

    console.log('in post', { props });
    const { posts, setPosts } = props;
    return (
        <div id="posts">
            <form onSubmit={handleSubmit}>
                <div>Add post:</div>
                <label>
                    Title:
                    <input type="text" name="title" onChange={(event) => {
                        setTitle(event.target.value);
                    }} />
                </label>
                <label>
                    Description:
                    <input type="text" name="title" onChange={(event) => {
                        setDescription(event.target.value);
                    }} />
                </label>
                <label>
                    Price:
                    <input type="text" name="title" onChange={(event) => {
                        setPrice(event.target.value);
                    }} />
                </label>
                <button className="button" type="submit">Submit</button>
            </form>

            <form onSubmit={deleteSubmit}>
                <div>Delete post:</div>
                <label>
                    Title:
                    <input type="text" name="title" onChange={(event) => {
                        setTitle(event.target.value);
                    }} />
                </label>
                <button className="button" type="submit">Submit</button>
            </form>
            <h1>Posts</h1>
            {
                posts.map(post => <div key={post._id}>
                    <div id="post-1">
                        <h3>{post.title}</h3>
                        <p>{post.description}</p>
                        <p>{post.price}</p>
                        <p>{post.author.username}</p>
                        <p>{post.updatedAt}</p>
                    </div>
                </div>)
            }
        </div>
    )
}

export default Posts;
